INSERT INTO `satdinfile` VALUES (11, 'writing the Class would be nicer, except that serialized\nclassed can not be read back by JDK 1.1.x. We have to resort\nto this hack instead.', 'Line', 'src/java/org/apache/log4j/spi/LoggingEvent.java', 195, 197, 'org.apache.log4j.spi.LoggingEvent', 'writePriority(ObjectOutputStream)', 'private void writePriority(ObjectOutputStream oos) throws java.io.IOException', '\r\n    oos.writeInt(priority.toInt());\r\n    Class clazz = priority.getClass();\r\n    if (clazz == Priority.class) {\r\n        oos.writeObject(null);\r\n    } else {\r\n        // writing the Class would be nicer, except that serialized\r\n        // classed can not be read back by JDK 1.1.x. We have to resort\r\n        // to this hack instead.\r\n        oos.writeObject(clazz.getName());\r\n    }\r\n', NULL);
INSERT INTO `satdinfile` VALUES (12, 'writing directly the Class object would be nicer, except that\nserialized a Class object can not be read back by JDK\n1.1.x. We have to resort to this hack instead.', 'Line', 'src/java/org/apache/log4j/spi/LoggingEvent.java', 240, 242, 'org.apache.log4j.spi.LoggingEvent', 'writePriority(ObjectOutputStream)', 'private void writePriority(ObjectOutputStream oos) throws java.io.IOException', '\r\n    oos.writeInt(priority.toInt());\r\n    Class clazz = priority.getClass();\r\n    if (clazz == Priority.class) {\r\n        oos.writeObject(null);\r\n    } else {\r\n        // writing directly the Class object would be nicer, except that\r\n        // serialized a Class object can not be read back by JDK\r\n        // 1.1.x. We have to resort to this hack instead.\r\n        oos.writeObject(clazz.getName());\r\n    }\r\n', NULL);
INSERT INTO `satdinfile` VALUES (13, 'convert to specified encoding - which may be sequence of\n8-bit chars, or multi-byte encodings like UTF-8 or UTF-16.\nThe receiving end had better be expecting whatever encoding\nis used here on the sending end!', 'Line', 'contribs/KitchingSimon/DatagramStringWriter.java', 117, 120, 'org.apache.log4j.net.DatagramStringWriter', 'write(String)', '\r\n    if (prefix != null) {\r\n        string = prefix + string;\r\n    }\r\n    byte[] rawData;\r\n    if (this.encoding == null) {\r\n        // convert to byte sequence using platform\'s default encoding\r\n        rawData = string.getBytes();\r\n    } else {\r\n        // convert to specified encoding - which may be sequence of\r\n        // 8-bit chars, or multi-byte encodings like UTF-8 or UTF-16.\r\n        // The receiving end had better be expecting whatever encoding\r\n        // is used here on the sending end!\r\n        rawData = string.getBytes(encoding);\r\n    }\r\n    DatagramPacket packet = new DatagramPacket(rawData, rawData.length, address, port);\r\n    if (this.ds != null) {\r\n        ds.send(packet);\r\n    } else {\r\n        LogLog.error(\"write: failed to create DatagramPacket\");\r\n    }\r\n', 'public void write(String string) throws IOException', NULL);
