INSERT INTO `satdinfile` VALUES (19, 'Print Stacktrace\nQuick Hack maybe there is a better/faster way?', 'Line', 'src/java/org/apache/log4j/gui/TextPaneAppender.java', 149, 150, 'org.apache.log4j.gui.TextPaneAppender', 'append(LoggingEvent)', '\r\n    String text = this.layout.format(event);\r\n    String trace = \"\";\r\n    // Print Stacktrace\r\n    // Quick Hack maybe there is a better/faster way?\r\n    if (event.throwable != null) {\r\n        event.throwable.printStackTrace(tp);\r\n        for (int i = 0; i < sw.getBuffer().length(); i++) {\r\n            if (sw.getBuffer().charAt(i) == \'\\t\')\r\n                sw.getBuffer().replace(i, i + 1, \"        \");\r\n        }\r\n        trace = sw.toString();\r\n        sw.getBuffer().delete(0, sw.getBuffer().length());\r\n    }\r\n    try {\r\n        if (fancy) {\r\n            textpane.setEditable(true);\r\n            textpane.insertIcon((ImageIcon) icons.get(event.priority));\r\n            textpane.setEditable(false);\r\n        }\r\n        doc.insertString(doc.getLength(), text + trace, (MutableAttributeSet) attributes.get(event.priority));\r\n    } catch (BadLocationException badex) {\r\n        System.err.println(badex);\r\n    }\r\n    textpane.setCaretPosition(doc.getLength());\r\n', 'public void append(LoggingEvent event)', NULL);
INSERT INTO `satdinfile` VALUES (20, 'Use a StringBuffer in lieu of String concatenation -- it is\nmuch more efficient this way.', 'Line', 'src/java/org/apache/log4j/HTMLLayout.java', 305, 306, 'org.apache.log4j.HTMLLayout', 'escapeHTMLTags(String)', '\r\n    // Check if the string is null or zero length -- if so, return\r\n    // what was sent in.\r\n    if (input == null || input.length() == 0) {\r\n        return input;\r\n    }\r\n    // Use a StringBuffer in lieu of String concatenation -- it is\r\n    // much more efficient this way.\r\n    StringBuffer buf = new StringBuffer(input.length() + 6);\r\n    char ch = \' \';\r\n    for (int i = 0; i < input.length(); i++) {\r\n        ch = input.charAt(i);\r\n        if (ch == \'<\') {\r\n            buf.append(\"&lt;\");\r\n        } else if (ch == \'>\') {\r\n            buf.append(\"&gt;\");\r\n        } else {\r\n            buf.append(ch);\r\n        }\r\n    }\r\n    return buf.toString();\r\n', 'private String escapeHTMLTags(String input)', NULL);
INSERT INTO `satdinfile` VALUES (21, 'If the helper is null, we\'ll inspect the System property \\\"java.version\\\" and\nfigure out which version of the VM we\'re running on.\nVersion strings are: [major version].[minor version].[bug fix revision]\nSo JDK 1.2: 1.2.0\nJDK 1.1: 1.1.0', 'Block', 'src/java/org/apache/log4j/helpers/VersionHelper.java', 47, 52, 'org.apache.log4j.helpers.VersionHelper', 'getInstance()', 'public static VersionHelper getInstance()', '\r\n    if (helper == null) {\r\n        /* If the helper is null, we\'ll inspect the System property \"java.version\" and\r\n			   figure out which version of the VM we\'re running on.\r\n			   Version strings are: [major version].[minor version].[bug fix revision]\r\n			   So JDK 1.2: 1.2.0\r\n			   JDK 1.1: 1.1.0 \r\n				*/\r\n        String prop = System.getProperty(VERSION_PROPERTY);\r\n        StringTokenizer st = new StringTokenizer(prop, \".\");\r\n        // Ignore the initial 1\r\n        st.nextToken();\r\n        String version = st.nextToken();\r\n        try {\r\n            /* Here we\'ll parse the number and decide which version helper to use */\r\n            switch(Integer.parseInt(version)) {\r\n                case 0:\r\n                case 1:\r\n                    helper = new VersionHelper11();\r\n                    break;\r\n                default:\r\n                    helper = new VersionHelper20();\r\n                    break;\r\n            }\r\n        } catch (NumberFormatException oops) {\r\n            helper = new VersionHelper11();\r\n        }\r\n    }\r\n    return helper;\r\n', NULL);
