INSERT INTO `satdinfile` VALUES (28, 'Inspect the System property \\\"java.version\\\" and figure out which\nversion of the VM we\'re running on.  Version strings are: [major\nversion].[minor version].[bug fix revision] So JDK 1.2: 1.2.0 JDK\n1.1: 1.1.0', 'Block', 'src/java/org/apache/log4j/helpers/VersionHelper.java', 53, 56, 'org.apache.log4j.helpers.VersionHelper', 'getInstance()', 'public static VersionHelper getInstance()', '\r\n    if (helper == null) {\r\n        /* Inspect the System property \"java.version\" and figure out which\r\n	 version of the VM we\'re running on.  Version strings are: [major\r\n	 version].[minor version].[bug fix revision] So JDK 1.2: 1.2.0 JDK\r\n	 1.1: 1.1.0 */\r\n        String prop = System.getProperty(VERSION_PROPERTY);\r\n        boolean java1 = true;\r\n        if (prop != null) {\r\n            int i = prop.indexOf(\'.\');\r\n            if (i != -1) {\r\n                if (prop.charAt(i + 1) != \'1\')\r\n                    java1 = false;\r\n            }\r\n        }\r\n        if (java1) {\r\n            helper = new VersionHelper11();\r\n        } else {\r\n            helper = new VersionHelper20();\r\n        }\r\n    }\r\n    return helper;\r\n', NULL);
