INSERT INTO `satdinfile` VALUES (154, 'move pointer', 'Line', 'src/java/org/apache/log4j/pattern/PatternParser.java', 229, 229, 'org.apache.log4j.pattern.PatternParser', 'parse()', 'public PatternConverter parse()', '\r\n    char c;\r\n    i = 0;\r\n    while (i < patternLength) {\r\n        c = pattern.charAt(i++);\r\n        switch(state) {\r\n            case LITERAL_STATE:\r\n                // In literal state, the last char is always a literal.\r\n                if (i == patternLength) {\r\n                    currentLiteral.append(c);\r\n                    continue;\r\n                }\r\n                if (c == ESCAPE_CHAR) {\r\n                    // peek at the next char.\r\n                    switch(pattern.charAt(i)) {\r\n                        case ESCAPE_CHAR:\r\n                            currentLiteral.append(c);\r\n                            // move pointer\r\n                            i++;\r\n                            break;\r\n                        case \'n\':\r\n                            currentLiteral.append(Layout.LINE_SEP);\r\n                            // move pointer\r\n                            i++;\r\n                            break;\r\n                        default:\r\n                            if (currentLiteral.length() != 0) {\r\n                                addToList(new LiteralPatternConverter(currentLiteral.toString()));\r\n                            // LogLog.debug(\"Parsed LITERAL converter: \\\"\"\r\n                            // +currentLiteral+\"\\\".\");\r\n                            }\r\n                            currentLiteral.setLength(0);\r\n                            // append %\r\n                            currentLiteral.append(c);\r\n                            state = CONVERTER_STATE;\r\n                            formattingInfo.reset();\r\n                    }\r\n                } else {\r\n                    currentLiteral.append(c);\r\n                }\r\n                break;\r\n            case CONVERTER_STATE:\r\n                currentLiteral.append(c);\r\n                switch(c) {\r\n                    case \'-\':\r\n                        formattingInfo.leftAlign = true;\r\n                        break;\r\n                    case \'.\':\r\n                        state = DOT_STATE;\r\n                        break;\r\n                    default:\r\n                        if ((c >= \'0\') && (c <= \'9\')) {\r\n                            formattingInfo.min = c - \'0\';\r\n                            state = MIN_STATE;\r\n                        } else {\r\n                            finalizeConverter(c);\r\n                        }\r\n                }\r\n                // switch\r\n                break;\r\n            case MIN_STATE:\r\n                currentLiteral.append(c);\r\n                if ((c >= \'0\') && (c <= \'9\')) {\r\n                    formattingInfo.min = (formattingInfo.min * 10) + (c - \'0\');\r\n                } else if (c == \'.\') {\r\n                    state = DOT_STATE;\r\n                } else {\r\n                    finalizeConverter(c);\r\n                }\r\n                break;\r\n            case DOT_STATE:\r\n                currentLiteral.append(c);\r\n                if ((c >= \'0\') && (c <= \'9\')) {\r\n                    formattingInfo.max = c - \'0\';\r\n                    state = MAX_STATE;\r\n                } else {\r\n                    LogLog.error(\"Error occured in position \" + i + \".\\n Was expecting digit, instead got char \\\"\" + c + \"\\\".\");\r\n                    state = LITERAL_STATE;\r\n                }\r\n                break;\r\n            case MAX_STATE:\r\n                currentLiteral.append(c);\r\n                if ((c >= \'0\') && (c <= \'9\')) {\r\n                    formattingInfo.max = (formattingInfo.max * 10) + (c - \'0\');\r\n                } else {\r\n                    finalizeConverter(c);\r\n                    state = LITER