INSERT INTO `satdinfile` VALUES (271, '\ncalling ourself should be safe and faster\nbut why risk it', 'Line', 'src/java/org/apache/log4j/pattern/CachedDateFormat.java', 168, 170, 'org.apache.log4j.pattern.CachedDateFormat', 'format(Date, StringBuffer, FieldPosition)', 'public StringBuffer format(Date date, StringBuffer sbuf, FieldPosition fieldPosition)', '\r\n    if (millisecondStart == UNRECOGNIZED_MILLISECOND_PATTERN) {\r\n        return formatter.format(date, sbuf, fieldPosition);\r\n    }\r\n    long now = date.getTime();\r\n    if ((now < (slotBegin + 1000L)) && (now >= slotBegin)) {\r\n        if (millisecondStart >= 0) {\r\n            int millis = (int) (now - slotBegin);\r\n            int cacheLength = cache.length();\r\n            milliBuf.setLength(0);\r\n            numberFormat.format(millis, milliBuf, fieldPosition);\r\n            for (int j = 0; j < milliDigits; j++) {\r\n                cache.setCharAt(millisecondStart + j, milliBuf.charAt(j));\r\n            }\r\n        }\r\n        sbuf.append(cache);\r\n    } else {\r\n        slotBegin = (now / 1000L) * 1000L;\r\n        // \r\n        // if earlier than 1970 and rounded toward 1970\r\n        // then move back one second\r\n        if ((now - slotBegin) < 0) {\r\n            slotBegin -= 1000;\r\n        }\r\n        cache.setLength(0);\r\n        formatter.format(new Date(slotBegin), cache, fieldPosition);\r\n        millisecondStart = findMillisecondStart(slotBegin, cache.toString(), formatter);\r\n        // \r\n        // calling ourself should be safe and faster\r\n        // but why risk it\r\n        formatter.format(date, sbuf, fieldPosition);\r\n    }\r\n    return sbuf;\r\n', NULL);
INSERT INTO `satdinfile` VALUES (272, 'if we can cache, so much the better', 'Line', 'src/java/org/apache/log4j/pattern/DatePatternConverter.java', 109, 109, 'org.apache.log4j.pattern.DatePatternConverter', 'setOptions(List)', 'public void setOptions(List optionList)', '\r\n    String patternOption;\r\n    if (optionList == null || optionList.size() == 0) {\r\n        // the branch could be optimized, but here we are making explicit\r\n        // that null values for patternOption are allowed.\r\n        patternOption = null;\r\n    } else {\r\n        patternOption = (String) optionList.get(0);\r\n    }\r\n    String pattern;\r\n    if (patternOption == null) {\r\n        pattern = \"yyyy-MM-dd HH:mm:ss,SSS\";\r\n    } else if (patternOption.equalsIgnoreCase(AbsoluteTimeDateFormat.ISO8601_DATE_FORMAT)) {\r\n        pattern = \"yyyy-MM-dd HH:mm:ss,SSS\";\r\n    } else if (patternOption.equalsIgnoreCase(AbsoluteTimeDateFormat.ABS_TIME_DATE_FORMAT)) {\r\n        pattern = \"HH:mm:ss,SSS\";\r\n    } else if (patternOption.equalsIgnoreCase(AbsoluteTimeDateFormat.DATE_AND_TIME_DATE_FORMAT)) {\r\n        pattern = \"dd MMM yyyy HH:mm:ss,SSS\";\r\n    } else {\r\n        pattern = patternOption;\r\n    }\r\n    try {\r\n        df = new SimpleDateFormat(pattern);\r\n    } catch (IllegalArgumentException e) {\r\n        logger.warn(\"Could not instantiate SimpleDateFormat with pattern \" + patternOption, e);\r\n        // detault for the ISO8601 format\r\n        pattern = \"yyyy-MM-dd HH:mm:ss,SSS\";\r\n        df = new SimpleDateFormat(\"yyyy-MM-dd HH:mm:ss,SSS\");\r\n    }\r\n    // if the option list contains a TZ option, then set it.\r\n    if (optionList != null && optionList.size() > 1) {\r\n        TimeZone tz = TimeZone.getTimeZone((String) optionList.get(1));\r\n        df.setTimeZone(tz);\r\n    }\r\n    // if we can cache, so much the better\r\n    if (CacheUtil.isPatternSafeForCaching(pattern)) {\r\n        df = new CachedDateFormat(df);\r\n    }\r\n', NULL);
