INSERT INTO `satdinfile` VALUES (287, 'Yes, it\'s ugly.  But all of these exceptions point to the same\nproblem: we can\'t create an Appender', 'Block', 'src/java/org/apache/log4j/xml/DOMConfigurator.java', 232, 233, 'org.apache.log4j.xml.DOMConfigurator', 'parseAppender(Element)', 'protected Appender parseAppender(Element appenderElement)', '\r\n    String className = subst(appenderElement.getAttribute(CLASS_ATTR));\r\n    LogLog.debug(\"Class name: [\" + className + \']\');\r\n    try {\r\n        Object instance = Loader.loadClass(className).newInstance();\r\n        Appender appender = (Appender) instance;\r\n        PropertySetter propSetter = new PropertySetter(appender);\r\n        appender.setName(subst(appenderElement.getAttribute(NAME_ATTR)));\r\n        NodeList children = appenderElement.getChildNodes();\r\n        final int length = children.getLength();\r\n        for (int loop = 0; loop < length; loop++) {\r\n            Node currentNode = children.item(loop);\r\n            /* We\'re only interested in Elements */\r\n            if (!isElement(currentNode)) {\r\n                continue;\r\n            }\r\n            Element currentElement = (Element) currentNode;\r\n            // Parse appender parameters\r\n            if (currentElement.getTagName().equals(PARAM_TAG)) {\r\n                setParameter(currentElement, propSetter);\r\n            // } else if (currentElement.getTagName().equals(ERROR_HANDLER_TAG)) {\r\n            // parseErrorHandler(currentElement, appender);\r\n            } else if (currentElement.getTagName().equals(APPENDER_REF_TAG)) {\r\n                String refName = subst(currentElement.getAttribute(REF_ATTR));\r\n                if (appender instanceof AppenderAttachable) {\r\n                    AppenderAttachable aa = (AppenderAttachable) appender;\r\n                    LogLog.debug(\"Attaching appender named [\" + refName + \"] to appender named [\" + appender.getName() + \"].\");\r\n                    aa.addAppender(findAppenderByReference(currentElement));\r\n                } else {\r\n                    LogLog.error(\"Requesting attachment of appender named [\" + refName + \"] to appender named [\" + appender.getName() + \"] which does not implement org.apache.log4j.spi.AppenderAttachable.\");\r\n                }\r\n            } else {\r\n                getLogger().debug(\"Handling nested <\" + currentElement.getTagName() + \"> for appender \" + appender.getName());\r\n                configureNestedComponent(propSetter, currentElement);\r\n            }\r\n        }\r\n        if (appender instanceof OptionHandler) {\r\n            ((OptionHandler) appender).activateOptions();\r\n        }\r\n        return appender;\r\n    }/* Yes, it\'s ugly.  But all of these exceptions point to the same\r\n       problem: we can\'t create an Appender */\r\n     catch (Exception oops) {\r\n        LogLog.error(\"Could not create an Appender. Reported error follows.\", oops);\r\n        return null;\r\n    }\r\n', NULL);
