INSERT INTO `satdinfile` VALUES (291, 'FIXME: We should not flood other appenders but at the same time\nshould output something meaningful (only once though).\nLogging not allowed:\ngetLogger().error(', 'Line', 'src/java/org/apache/log4j/AppenderSkeleton.java', 213, 216, 'org.apache.log4j.AppenderSkeleton', 'doAppend(LoggingEvent)', '\r\n    // WARNING: The guard check MUST be the first statement in the\r\n    // doAppend() method.\r\n    // prevent re-entry.\r\n    if (guard) {\r\n        return;\r\n    }\r\n    try {\r\n        guard = true;\r\n        if (closed) {\r\n            // FIXME: We should not flood other appenders but at the same time\r\n            // should output something meaningful (only once though).\r\n            // Logging not allowed:\r\n            // getLogger().error(\r\n            // \"Attempted to append to closed appender named [{}].\", name);\r\n            return;\r\n        }\r\n        if (!isAsSevereAsThreshold(event.getLevel())) {\r\n            return;\r\n        }\r\n        Filter f = this.headFilter;\r\n        FILTER_LOOP: while (f != null) {\r\n            switch(f.decide(event)) {\r\n                case Filter.DENY:\r\n                    return;\r\n                case Filter.ACCEPT:\r\n                    break FILTER_LOOP;\r\n                case Filter.NEUTRAL:\r\n                    f = f.getNext();\r\n            }\r\n        }\r\n        this.append(event);\r\n    } finally {\r\n        guard = false;\r\n    }\r\n', 'public synchronized void doAppend(LoggingEvent event)', NULL);
